    &:after{
       content:'';
       display:inline-block;
       color: #94a657;
       background:#fff;
       font-family: 'FontAwesome';
       padding:3px;
       border-radius: 50%;
       height:20px;
       width:20px;
       margin-left:8px;
       padding-left: 4.5px;
       padding-right:1.5px;
    }
    &.playing{
        &:after{
          padding:3px;
          content:'';
       }
    }
 }

